INSERT INTO `sys_log` VALUES (6438, '发表简讯', 'INFO', 'me.mcx.modules.blog.admin.rest.TipsoonController.insertTipsoon()', '{\"content\":\"<p>哈佛大学医学院的研究表明，如果设置多个闹钟，比如隔 5 分钟就设定一个闹钟，持续多个闹钟，人在反复惊醒之后再睡下，可能会导致慢性疲劳。因为被闹钟反复惊醒后，人体就会分泌一种名为“腺苷”的化合物。腺苷含量上升会使人感到愈加困倦，反而更容易陷入睡眠和觉醒之间的循环，难以完全清醒。这也可能导致按掉闹钟后再眯几分钟，结果一睁眼却已经是几个小时之后了。</p>\",\"createTime\":1711039480918,\"id\":\"3e6cde7986bfbc91f2794803460df887\",\"imgUrl\":\"http://xn--7gq6m992g.xn--9krq6qgr9bpbg.xn--jduy7zg5y.xn--6qq986b3xl/20240322_1711039449792.jpg\",\"title\":\"多个闹钟可能导致慢性疲劳\",\"userId\":\"1\"}', '192.168.219.93', 383, 'admin', '内网IP', 'MSEdge 122', NULL, '2024-03-22 00:44:41');
